import mongoose, {Schema} from 'mongoose';
import timestamps from 'mongoose-timestamp'
import {composeWithMongoose} from 'graphql-compose-mongoose'

export const ClassroomActivitiesSchema = new Schema(
    {
        Start_Time:{
            type: String,
            trim: true,
            required: true
        },
        Date:{
            type: String,
            trim: true,
            required: true,
        },
        Week_Day:{
            type:String,
            trim: true,
            required: true,
        },
        Date_Time:{
            type: Date,
            required: true,
        },
        Activity_Type:{
            type: String,
            required: true,
        },
        Activity_ID:{
            type: Schema.Types.ObjectId,
            required: true
        }
    },
    {
        _id: false
    }
)
export const DaySchema = new Schema({
    _id:{
        type: Schema.Types.ObjectId,
        required: true
    },
    Start_Time:{
        type: String,
        trim: true,
        required: true
    },
    Date:{
        type:String,
        trim: true,
        required: true
    },
    Week_Day:{
        type:String,
        trim: true,
        required:true,
    },
    Date_Time_Days_Only:{
        type: Date,
        required: true
    },
    Building:{
        type: String,
        trim: true,
        required: true,
    },
    Building_Obj_id:{
        type: Schema.Types.ObjectId,
        required: true,
    },
    Classroom:{
        type: String,
        required: true,
        trim: true
    },
    Classroom_Obj_id:{
        type: Schema.Types.ObjectId,
        required: true
    },
    Classroom_Activities:{
        type: [ClassroomActivitiesSchema],
        required: true,
        index: true
    },
    ClassroomCount:{
        type: Number,
        required: true
    }
},{
    collection: '2019-09-18'
})

DaySchema.pre('validate', function (next){
    this.ClassroomCount = this.Classroom_Activities.length;
    console.log(this.ClassroomCount)
    next();
})
DaySchema.plugin(timestamps)

export const Day = mongoose.model('Day', DaySchema)
export const DayTC = composeWithMongoose(Day)

DayTC.addFields({
    activityCount:{
        type: 'Int',
        resolve: async (source, args, context, info)=>{
            const count = await Day.aggregate([
                {$sum: '$Classroom_Activities'}
            ])
            return count
        }

    }
}
)

